import pandas as pdimport numpy as npfrom scipy import statsfrom sklearn.model_selection import train_test_splitfrom sklearn.preprocessing import StandardScaler#standard scaler X train and X test for SVMdef SVM_preprocess(X_train,X_test):    sc = StandardScaler()    X_train_sc = sc.fit_transform(X_train)    X_test_sc = sc.transform(X_test)    #y_train= y_train.ravel()    #y_test = y_test.ravel()    return X_train_sc,X_test_scdef ANN_preprocess(X_train, X_test):    mean = X_train.mean(axis=0)    X_train_norm =(X_train-mean)    std = X_train_norm.std(axis=0)    X_train_norm /= std    X_test_norm = (X_test- mean) / std    return X_train_norm,X_test_norm